import React, { useState } from 'react';

const ContactPage = () => {
  const [showQuestionForm, setShowQuestionForm] = useState(false);
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    questionTopic: '',
    questionReason: '',
    alreadyPurchased: 'No',
    message: '',
  });

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    // Here you can add your logic to handle form submission,
    // such as sending the data to a server or displaying a thank you message.
    console.log('Form submitted with data:', formData);
    // Reset the form fields after submission
    setFormData({
      name: '',
      email: '',
      questionTopic: '',
      questionReason: '',
      alreadyPurchased: 'No',
      message: '',
    });
    setShowQuestionForm(false);
  };

  const openQuestionForm = () => {
    setShowQuestionForm(true);
  };

  const closeQuestionForm = () => {
    setShowQuestionForm(false);
  };

  return (
    <div className="contact-page">
      <h1>Contact Us</h1>
      <div className="contact-info">
        <div className="contact-section gray-box">
          <h2>Customer Care</h2>
          <p>For assistance with existing orders or inquiries:</p>
          <p>Call: <a href="tel:+1234567890">123-456-7890</a></p>
        </div>
        <div className="contact-section gray-box">
          <h2>Sales</h2>
          <p>For inquiries related to new orders or services:</p>
          <p>Call: <a href="tel:+9876543210">987-654-3210</a></p>
        </div>
      </div>
      <button className="open-form-button" onClick={openQuestionForm}>
        Submit a Question
      </button>
      {showQuestionForm && (
        <div className="question-form-modal">
          <div className="question-form-box">
            <h2>Submit a Question</h2>
            <form onSubmit={handleSubmit}>
              {/* Your form fields */}
              {/* Full name */}
              <div className="form-group">
                <label htmlFor="name">Full name</label>
                <input
                  type="text"
                  id="name"
                  name="name"
                  value={formData.name}
                  onChange={handleInputChange}
                  required
                />
              </div>
              {/* Email */}
              <div className="form-group">
                <label htmlFor="email">Email</label>
                <input
                  type="email"
                  id="email"
                  name="email"
                  value={formData.email}
                  onChange={handleInputChange}
                  required
                />
              </div>
              {/* Question */}
              <div className="form-group">
                <label htmlFor="questionTopic">Select a topic</label>
                <select
                  id="questionTopic"
                  name="questionTopic"
                  value={formData.questionTopic}
                  onChange={handleInputChange}
                  required
                >
                  <option value="">Select a topic</option>
                  {/* Add your topic options here */}
                </select>
              </div>
              {/* Reason */}
              <div className="form-group">
                <label htmlFor="questionReason">Select a reason</label>
                <select
                  id="questionReason"
                  name="questionReason"
                  value={formData.questionReason}
                  onChange={handleInputChange}
                  required
                >
                  <option value="">Select a reason</option>
                  {/* Add your reason options here */}
                </select>
              </div>
              {/* Already made a purchase? */}
              <div className="form-group">
                <label>Already made a purchase?</label>
                <div>
                  <label>
                    <input
                      type="radio"
                      name="alreadyPurchased"
                      value="Yes"
                      checked={formData.alreadyPurchased === 'Yes'}
                      onChange={handleInputChange}
                    />
                    Yes
                  </label>
                  <label>
                    <input
                      type="radio"
                      name="alreadyPurchased"
                      value="No"
                      checked={formData.alreadyPurchased === 'No'}
                      onChange={handleInputChange}
                    />
                    No
                  </label>
                </div>
              </div>
              {/* Message */}
              <div className="form-group">
                <label htmlFor="message">Message</label>
                <textarea
                  id="message"
                  name="message"
                  value={formData.message}
                  onChange={handleInputChange}
                  required
                />
              </div>
              <button type="submit">Submit</button>
            </form>
            <button className="close-form-button" onClick={closeQuestionForm}>
              Close
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default ContactPage;
