import React, { useState } from "react";
import styles from '../styles/Navbar.module.css'
import { Link } from "react-router-dom";

function Navbar() {
    const [isMenuOpen, setMenuOpen] = useState(false);

    const toggleMenu = () => {
        setMenuOpen(!isMenuOpen);
    };

    return (
        <header>
            <nav className={styles.nav}>
                <h1 className="slogan">THE LEGAL SAVANNAH | Sheria Mkononi</h1>
                <button className={`${styles.button} ${styles.navToggle}`} onClick={toggleMenu}>
                    <span className={styles.menuIcon}></span>
                </button>
                <ul className={`nav-items ${isMenuOpen ? styles.navOpen : ''}`}>
                    <li className="nav-item"><button className={styles.btn}>Advocates</button></li>
                    <li className="nav-item"><button className={styles.btn}><Link to="/create">About Us</Link></button></li>
                    <li className="nav-item"><button className={styles.btn}>Contact Us</button></li>
                    <li className="nav-item"><button className={styles.button}><span><Link to="/username">Login</Link></span></button></li>
                </ul>
            </nav>
        </header>
    );
}

export default Navbar;
